def calculate_lead_score(job_title, country, company_size):
  """Calculates a lead score based on job title, country, and company size.

  Args:
    job_title: The job title of the lead.
    country: The country of the lead.
    company_size: The company size of the lead (number of employees).

  Returns:
    The calculated lead score.

  Raises:
    ValueError: If any of the input parameters are invalid.
  """

  if not isinstance(job_title, str) or not job_title:
    raise ValueError("Invalid job_title: Must be a non-empty string.")
  if not isinstance(country, str) or not country:
    raise ValueError("Invalid country: Must be a non-empty string.")
  if not isinstance(company_size, int) or company_size <= 0:
    raise ValueError("Invalid company_size: Must be a positive integer.")

  if job_title == "Software Engineer":
    score = 10
  elif country.lower().endswith("a"):
    score += 5
  elif company_size > 1000:
    score += 20

  return score
