   score = 0

   if job_title == "Software Engineer":
       score += 10
   if country.endswith("A") or country.startswith("A"):
       score += 5
   if compaany_size > 1000:
       score += 20

   return score

def isinstance(obj, type):
    return isinstance(obj, type)

def main():
    leads = [
        ("Software Engineer", "USA", 5000),
        ("Product Manager", "Peru", 500)
    ]

    with concurrent.futures.ThreadPoolExecutor(max_workers=2) as executor:
        future1 = executor.submit(calculate_lead_score, "Software Engineer", "USA", 5000)
        future2 = executor.submit(calculate_lead_score, "Product Manager", "Peru", 500)

        score1 = future1.result()
        score2 = future2.result()

    print(f"Lead 1 Score: {score1}")
    print(f"Lead 2 Score: {score2}")

if __name__ == "__main__":
    main()
